var Batt = parseFloat(msg.payload.homeOwnConsumptionFromBattery.toFixed(0));
var Netz = parseFloat(msg.payload.homeOwnConsumptionFromGrid.toFixed(0));
var PV = parseFloat(msg.payload.homeOwnConsumptionFromPV.toFixed(0));

msg.payload = Batt + Netz + PV;
msg.topic = "homeOwnConsumption";
return msg;

var wirkungsGrad = msg.payload.totalACActivePower / msg.payload.totalDCPower * 100;
msg.payload = parseFloat(wirkungsGrad.toFixed(1));
msg.topic = "totalEfficiency";
return msg;

for (let key in msg.payload) {
    if (key !== "key" && key !== "info") {
        let msg1 = { payload: {} };
        msg1.payload = msg.payload[key];
        msg1.topic = "devices/Kostal/" + key;
        node.send(msg1)
    }
}
return;

msg.payload = msg.payload.powerDC1 + msg.payload.powerDC2;
msg.topic = "totalPVPower";
return msg;

msg.measurement = msg.topic.replace("devices/", "");
msg.measurement = msg.measurement.replace("/", ".");
return msg;

// Topic Pre-Fix l√∂schen und timestamp setzen
msg.topic = msg.topic.replace("devices/Kostal/cmd/", "");
flow.set(msg.topic, msg.payload);
flow.set("lastmqtt", Date.now());
flow.set("externalBatMgt", true);

return msg;

// automatic ausschalten
flow.set("externalBatMgt", msg.payload);
flow.set("batteryChargePowerDCAbsolute", -1);
flow.set("batteryMaxChargePowerLimit", -1);
flow.set("batteryMaxDischargePowerLimit", -1);
flow.set("minimumSoc", -1);
flow.set("maximumSoc", -1);

return msg;
